mapper.insert:


SELECT MAX(product_idx) FROM product

INSERT 
	INTO product
	VALUES (
		#{product_idx} + 1
		,#{product_name}
		,#{product_brand}
		,#{product_size}
		,#{product_price}
		,#{product_release_price}
		,#{product_amount}
		,0
		,#{product_exp}
		,#{product_detail_exp}
		,#{product_color}
		,#{product_discount_price}
		,now()
		,0
	)



SELECT MAX(image_idx) FROM image

INSERT 
	INTO image (image_idx, product_idx, image_main_file, image_real_file1)
	VALUES (
		#{image_idx} + 1
		,#{product_idx} + 1
		,#{image.image_main_file}
		,#{image.image_real_file1}

	)

mapper.select:
SELECT image_main_file, image_real_file1, image_real_file2  
	FROM image 
	WHERE product_idx = #{product_idx}

mapper.update:
UPDATE orderlist
 SET order_progress = "환불완료"
 WHERE order_idx = #{order_idx}

mapper.select:
SELECT product_idx
	FROM orderlist
	WHERE order_idx = #{order_idx}
